package chess.pieces;

import boardgame.Board;
import boardgame.Position;
import chessEntities.ChessPiece;
import chessEntities.Color;

public class Queen extends ChessPiece{

	public Queen(Board board, Color color) {
		super(board, color);
		// TODO Auto-generated constructor stub
	}

	@Override
	public boolean[][] possibleMoves() {
		// Movimentos da rainha
		
		boolean[][] mat = new boolean[getBoard().getRows()][getBoard().getColumns()];
		// variavel auxiliadora
		
		Position p = new Position(0,0);
		
		// loop que verifica as posições no  board e atribui true
		
		// MOVIMENTO VERTICAL DA PEÇA RAINHA
		p.setValues(position.getRow() - 1, position.getColumn());
		while (getBoard().positionExists(p) && !getBoard().thereIsAPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
			p.setRow(p.getRow() - 1);
		}
		if (getBoard().positionExists(p) && isThereOpponentPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
		}
		
		
		// MOVIMENTO LATERAL ESQUERDO DA RAINHA
		p.setValues(position.getRow(), position.getColumn() - 1);
		while (getBoard().positionExists(p) && !getBoard().thereIsAPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
			p.setColumn(p.getColumn() - 1);
		}
		if (getBoard().positionExists(p) && isThereOpponentPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
		}
		//MOVIMENTO LATERAL DIREITO DA RAINHA
		p.setValues(position.getRow(), position.getColumn() + 1);
		while (getBoard().positionExists(p) && !getBoard().thereIsAPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
			p.setColumn(p.getColumn() + 1);
		}
		if (getBoard().positionExists(p) && isThereOpponentPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
		}
		//MOVIMENTO VERTICAL PARA BAIXO RAINHA
		p.setValues(position.getRow() + 1, position.getColumn());
		while (getBoard().positionExists(p) && !getBoard().thereIsAPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
			p.setRow(p.getRow() + 1);
		}
		if (getBoard().positionExists(p) && isThereOpponentPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
		}
		
		//MOVIMENTAÇÕES DIAGONAIS DA RAINHA	
		// MOVIMENTO DIAGONAL SUPERIOR ESQUERDO DA PEÇA RAINHA
		p.setValues(position.getRow() - 1, position.getColumn()  - 1);
		while (getBoard().positionExists(p) && !getBoard().thereIsAPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
			p.setValues(p.getRow() - 1, p.getColumn() - 1);
		}
		if (getBoard().positionExists(p) && isThereOpponentPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
		}
		
		// MOVIMENTO DIAGONAL SUPERIOR DIREITO DA PEÇA RAINHA
		p.setValues(position.getRow() - 1, position.getColumn()  + 1);
		while (getBoard().positionExists(p) && !getBoard().thereIsAPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
			p.setValues(p.getRow() - 1, p.getColumn() + 1);
		}
		if (getBoard().positionExists(p) && isThereOpponentPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
		}
		
		// MOVIMENTO DIAGONAL INFERIOR ESQUERDO DA PEÇA RAINHA
		p.setValues(position.getRow() + 1, position.getColumn()  - 1);
		while (getBoard().positionExists(p) && !getBoard().thereIsAPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
			p.setValues(p.getRow() + 1, p.getColumn()  - 1);
		}
		if (getBoard().positionExists(p) && isThereOpponentPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
		}
		
		// MOVIMENTO DIAGONAL INFERIOR DIREITO DA PEÇA RAINHA
		p.setValues(position.getRow() + 1, position.getColumn()  + 1);
		while (getBoard().positionExists(p) && !getBoard().thereIsAPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
			p.setValues(p.getRow() + 1, p.getColumn()  + 1);
		}
		if (getBoard().positionExists(p) && isThereOpponentPiece(p)) {
			mat[p.getRow()][p.getColumn()] = true;
		}
		return mat;
	} //problema com movimentos laterais esquerdos inferiores
	
	@Override
	public String toString() {
		return "Q";
	}

}
